(145, 'Maniement d''une arme exotique (Filet)', 'Lorsqu’il utilise l’arme exotique choisie, le personnage joue ses jets d’attaque normalement.\r\nQuiconque combat avec une arme sans être formé à son maniement subit un malus de –4 aux jets d’attaque.\r\n', 'Ce don peut être choisi plusieurs fois, mais ses effets ne se cumulent pas. Il s’applique à chaque fois à une nouvelle arme exotique.\r\nLe maniement de l’épée bâtarde ou de la hache de guerre naine a pour condition supplémentaire une Force de 13+.\r\nUn guerrier peut choisir Maniement d’une arme exotique en tant que don supplémentaire.', 1, NULL, NULL, NULL, NULL, NULL, NULL, 1, NULL),
(146, 'Maniement d''une arme exotique (Shuriken)', 'Lorsqu’il utilise l’arme exotique choisie, le personnage joue ses jets d’attaque normalement.\r\nQuiconque combat avec une arme sans être formé à son maniement subit un malus de –4 aux jets d’attaque.\r\n', 'Ce don peut être choisi plusieurs fois, mais ses effets ne se cumulent pas. Il s’applique à chaque fois à une nouvelle arme exotique.\r\nLe maniement de l’épée bâtarde ou de la hache de guerre naine a pour condition supplémentaire une Force de 13+.\r\nUn guerrier peut choisir Maniement d’une arme exotique en tant que don supplémentaire.', 1, NULL, NULL, NULL, NULL, NULL, NULL, 1, NULL);
INSERT INTO `dnd35_dons` (`id`, `nom`, `description`, `special`, `bbaPrerequis`, `forPrerequis`, `dexPrerequis`, `conPrerequis`, `intPrerequis`, `sagPrerequis`, `chaPrerequis`, `donSuppGuerrier`, `niveauLanceurSort`) VALUES
(147, 'Arme de prédilection (Attaque à mains nues)', 'Le personnage choisit une arme. Il peut aussi choisir les attaques à mains nues, la lutte ou les rayons.\r\nLe personnage bénéficie d’un bonus de +1 à tous ses jets d’attaque lorsqu’il utilise son arme de prédilection.', 'Ce don peut être choisi plusieurs fois, mais ses effets ne se cumulent pas. Il s’applique à chaque fois à une nouvelle arme.\r\nUn guerrier peut choisir Arme de prédilection en tant que don supplémentaire. Un guerrier ne peut se choisir le don Spécialisation martiale que pour une de ses armes de prédilection.', 1, NULL, NULL, NULL, NULL, NULL, NULL, 1, NULL),
(148, 'Arme de prédilection (Gantelet)', 'Le personnage choisit une arme. Il peut aussi choisir les attaques à mains nues, la lutte ou les rayons.\r\nLe personnage bénéficie d’un bonus de +1 à tous ses jets d’attaque lorsqu’il utilise son arme de prédilection.', 'Ce don peut être choisi plusieurs fois, mais ses effets ne se cumulent pas. Il s’applique à chaque fois à une nouvelle arme.\r\nUn guerrier peut choisir Arme de prédilection en tant que don supplémentaire. Un guerrier ne peut se choisir le don Spécialisation martiale que pour une de ses armes de prédilection.', 1, NULL, NULL, NULL, NULL, NULL, NULL, 1, NULL),
