INSERT INTO `dnd35_objetsArt` (`id`, `prcMin`, `prcMax`, `valeur`, `exemples`) VALUES
(1, 1, 10, '1d10x10po', 'aiguière en argent;statuette en os ou en ivoire;bracelet d''or fin'),
(2, 11, 25, '3d6x10po', 'vêtements tissés de fil d''or;masque de velour noir agrémenté de nombreuses citrines;calice en argent serti de lapis-lazuli'),
(3, 26, 40, '1d6x100po', 'grande tapisserie en laine;chope en laiton incrustée de motifs en jade'),
(4, 41, 50, '1d10x100po', 'peigne en argent serti de pierres de lune;épée longue à lame plaquée d''argent et au pommeau taillé dans le jais'),
(5, 51, 60, '2d6x100po', 'harpe en bois exotique sculptée et ornée d''ivoire et de zircons; statuette en or massif'),
(6, 61, 70, '3d6x100po', 'peigne en or en forme de dragon avec un oeil de grenat;bouchon en or et topaze;dague d''apparat en électrum avec un rubi enchâssé dans son pommeau'),
(7, 71, 80, '4d6x100po', 'bandeau sur lequel un faux oeil a été tissé à l''aide de saphirs et de pierres de lune;opale de feu fixées à une chaîne d''or fin;peinture de maître'),
(8, 81, 85, '5d6x100po', 'manteau de soie et de velours orné de nombreuses pierres de lune;pendentif en saphir fixé à une chaîne en or'),
(9, 86, 90, '1d4x1000po', 'gant brodé et garni de nombreuses pierres fines;bracelet de cheville serti de pierres fines;boîte à musique en or'),
(10, 91, 95, '1d6x1000po', 'serre-tête en or serti de quatre aigue-marines;collier de petites perles roses'),
(11, 96, 99, '2d4x1000po', 'couronne en or sertie de joyaux;anneau en électrum serti d''une pierre précieuse'),
(12, 100, 100, '2d6x1000po', 'anneau d''or serti d''un rubis;coupe en or sertie d''émeraudes');
